import './Settings.css'
import TagPicker from 'rsuite/TagPicker';
import 'rsuite/TagPicker/styles/index.css';
import {useEffect} from "react";
let log;

export default function Settings () {

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('http://localhost:81/filters.php');
                const jsonData = await response.json();
                setRedirectTo(jsonData);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchData();
    }, []);

    // document.addEventListener("DOMContentLoaded", function() {
    //     document.getElementById("userSelect").addEventListener("change", function() {
    //
    //     });
    // });

    // window.addEventListener("load", function() {
    //     let selectedUser = localStorage.getItem("login");
    //     if (selectedUser) {
    //         document.getElementById("userSelect").value = selectedUser;
    //     }
    // });
    function saveLog(){
        localStorage.clear();
        let selectedUser = this.value; // Получаем выбранного пользователя
        const myJson = {
            username: selectedUser
        };
        // Отправляем выбранного пользователя на сервер для авторизации
        fetch("http://localhost:81/login.php", {
            method: "POST",
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(myJson) // Отправляем только имя пользователя
        })
            .then(response => {
                if (response.ok) {
                    return response.text();
                } else {
                    throw new Error("Ошибка сети");
                }
            })
            .then(data => {
                console.log(data);
                log=myJson.username;
            })
            .catch(error => {
                // Ошибка сервера
                alert("Ошибка: " + error.message);
            });
        localStorage.setItem('login', selectedUser);
    }

    return (
        <main>
            <div className='Settings'>
                <h1 className='Text'>Choose user</h1>
                <select id="userSelect" className="UserSelector">
                    <option class="NameText">user1</option>
                    <option class="NameText">user2</option>
                </select>
                <button onClick={saveLog}>SAVE LOG</button>
            </div>
        </main>
    )
}
