import React, { useState, useEffect } from 'react';
import toast from './img/toast.jpg'
import pizza from './img/pitsa.jpg'
import pelmeni from './img/pelmeni.jpg'
import friedEggs from './img/FriedEggs.jpeg'
import { Link } from 'react-router-dom'

function RecipesList () {
    const [redirectTo, setRedirectTo] = useState("/");
    let images = [];
    images.push(pelmeni);
    images.push(pizza);
    images.push(toast);
    images.push(friedEggs);
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('http://localhost:81/recipes.php');
                const jsonData = await response.json();
                setRedirectTo(jsonData);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchData();
    }, []);

    let name = "soapd";
    let id = 333;
    let i = 0;
    let finalStr = [];

    document.addEventListener("DOMContentLoaded", function() {
        fetch('http://localhost:81/calorize.php')
            .then(response => response.json())
            .then(data => {
                // Получаем ответ от сервера и выводим его в консоль
                console.log(data);
            })
            .catch(error => {
                console.error('Ошибка:', error);
            });
    });

    function swapping(){
        for (let i = 0; i < redirectTo.length; i++){
            finalStr.push('/food/' + String(redirectTo[i].dish_id));
        }
    }
    swapping();

    return (
        <dl className="ListFood">
            {(() => {
                const links = [];
                for (let i = 0; i < finalStr.length; i++) {
                    const id = finalStr[i];
                    links.push(
                            <Link className="FoodList" to={id}><Food id={redirectTo[i].dish_id} name={redirectTo[i].dish_name} image={redirectTo[i].dish_image_url}/></Link>
                    );
                }
                return links;
            })()}
