            {/*<Link className="FoodList" to={finalStr}><Food id={redirectTo[0].dish_id} name={redirectTo[0].dish_name} image={pizza}/></Link>*/}
            {/*<Link className="FoodList" to="/food"><Food id={id} name={name} image={toast}/></Link>*/}
            {/*<Link className="FoodList" to="/food"><Food id={id} name={name} image={toast}/></Link>*/}
