import './Settings.css'
import TagPicker from 'rsuite/TagPicker';
import 'rsuite/TagPicker/styles/index.css';

export default function Settings () {
    // const Users = ['user1', 'user2'].map(
    //     item => ({ label: item, value: item })
    // );

    document.addEventListener("DOMContentLoaded", function() {
        document.getElementById("userSelect").addEventListener("change", function() {
            let selectedUser = this.value; // Получаем выбранного пользователя
            console.log(selectedUser);
            // Отправляем выбранного пользователя на сервер для авторизации
            fetch("http://localhost:81/login.php", {
                method: "POST",
                body: "username=" + selectedUser // Отправляем только имя пользователя
            })
                .then(response => {
                    if (response.ok) {
                        return response.text();
                    } else {
                        throw new Error("Ошибка сети");
                    }
                })
                .then(data => {
                    console.log(data);
                })
                .catch(error => {
                    // Ошибка сервера
                    alert("Ошибка: " + error.message);
                });
        });
    });

    return (
        <main>
            <div className='Settings'>
                <h1 className='Text'>Choose user</h1>
                <select id="userSelect" className="UserSelector">
                    <option class="NameText">user1</option>
                    <option class="NameText">user2</option>
                </select>
            </div>
        </main>
    )
}
